(()=>{"use strict";document.body.addEventListener("click",(e=>{if(!e.target.matches(".fancyboxModal"))return;e.preventDefault();const t=e.target,l=document.querySelector(t.getAttribute("href")),o=l.closest(".modal--opened"),c=document.querySelector(".overlay");o.style.display="block",c.style.display="block",l.style.display="block";const s=e=>{e.target.matches(".modal__close, .overlay")&&(o.style.display="none",c.style.display="none",l.style.display="none",document.body.removeEventListener("click",s))};document.body.addEventListener("click",s)}))})();